function error(er) {
    game.modding.terminal.echo(`[[;#FF7733;]${er}]`);
}

function echo(ec, c) {
    game.modding.terminal.echo(`[[bg;${c ? c : "#61ff61"};]${ec}]`);
}

function warn(wa) {
    game.modding.terminal.echo(`[[g;#E6DB74;]${wa}]`);
}

function setSpectate(ship, type) {
    ship.custom.spectate = true;
    ship.custom.type = type ? type : ship_lists.admin.includes(ship.type) ? 605 : ship.type;
    ship.set({ type: 191, collider: false, crystals: 0, stats: 0 });
}

function getMaxCrystals(type) {
    const maxcrystals = Math.pow(Math.trunc(type / 100), 2) * 20;
    return game.custom.gemspickup ? maxcrystals - 1 : maxcrystals;
}

function getShipData(ship, color = true) {
    return color ? `[[gb;#ffb36c;]Name: ${ship.name}, ID: ${ship.id}]` : `Name: ${ship.name}, ID: ${ship.id}`;
}

function updateShip(ship, type, upgrade = true) {
    ship.custom.spectate = false;
    ship.custom.type = type;
    ship.set({ type: type, shield: 99999, stats: ship_lists.admin.includes(type) ? 0 : Number(String(type).charAt(0).repeat(8)), crystals: getMaxCrystals(type), collider: true, vx: 0, vy: 0, });
}

function switchShip(ship, next = false) {
    if (ship.custom.spectate || ship.type === 191) return updateShip(ship, ship.custom.type);

    const ship_list = ship_lists.default;
    const currentIndex = ship_list.indexOf(ship_lists.speedster.includes(ship.type) || ship_lists.admin.includes(ship.type) && ship.type !== 605 ? 605 : ship.type);
    const type = (next ? currentIndex + 1 : currentIndex - 1 + ship_list.length) % ship_list.length;
    updateShip(ship, ship_list[type]);
}

function warp(ship) {
    const registeredShips = game.ships.filter(s => s.custom.registered);

    if (registeredShips.length <= 1) return displayMessage(ship, "Nobody to Teleport");

    let tp = (ship.custom.lastTeleported + 1) % registeredShips.length || 0;

    if (registeredShips[tp].id === ship.id) {
        tp = (tp + 1) % registeredShips.length;
    }

    ship.custom.lastTeleported = tp;
    const targetShip = registeredShips[tp];

    if (!ship.custom.spectate) setSpectate(ship);

    ship.set({
        x: targetShip.x,
        y: targetShip.y,
        vx: 0,
        vy: 0
    });
}

function idleShip(ship, give = true, x0y0 = false, msg) {
    if (!give && ship.custom.idle) {
        ship.custom.idle = false;
        ship.custom.registered = true;
        ship.set({ idle: false, vx: 0, vy: 0 });
        ship.setUIComponent(_options);
        ship.setUIComponent(heal);
        if (ship.custom.admin) ship.setUIComponent(adminn);
        displayMessage(ship, msg || "You've been unfrozen");
        return;
    }

    const _UIs = ["adminShip", "options", "heal"];

    Object.keys(UIs).forEach(ids => {
        _UIs.push(ids);
    });

    _UIs.forEach(id => {
        ship.setUIComponent({ id, ...Disable_UI });
    });

    ship.custom.idle = true;
    ship.custom.registered = false;
    if (!ship.custom.spectate) setSpectate(ship, 605);
    ship.set({ idle: true, vx: 0, vy: 0 });
    if (x0y0) ship.set({ x: 0, y: 0 });
    displayMessage(ship, msg || "You've been frozen");
}

function displayMessage(ship, text, duration = 3000) {
    clearTimeout(ship.custom.msgTimeout);
    ship.setUIComponent({
        id: "text",
        position: [25, 82, 50, 5],
        visible: true,
        components: [
            {
                type: "text",
                position: [0, 0, 100, 100],
                color: "hsl(0, 100%, 75%)",
                value: text,
                align: "center",
            }
        ]
    });
    ship.custom.msgTimeout = setTimeout(() => {
        ship.setUIComponent({ id: "text", ...Disable_UI });
    }, duration);
}

this.tick = function (game) {
    for (let ship of game.ships) {
        if (!ship.alive || ship.custom.kicked || ship === null || ship.id === null || ship.type === 191) continue;
        if (ship_lists.admin.includes(ship.type) && !ship.custom.admin) return kick(ship.id, "Use admin ship without Permission");
        if (game.step % (game.ships.length * 2) === 0 && game.custom.gemspickup && !ship_lists.admin.includes(ship.type)) {
            let maxCrystals = getMaxCrystals(ship.type);
            if (ship.crystals === (maxCrystals + 1)) {
                ship.set({ crystals: maxCrystals });
            }
        }
    }
};

game.modding.tick = function (t) {
    this.game.tick(t);
    if (this.context.tick != null) {
        this.context.tick(this.game);
    }
};

const UIs = {
    nextShip: { label: "Next Ship", shortcut: "1" },
    previousShip: { label: "Prev Ship", shortcut: "2" },
    reset: { label: "Reset", shortcut: "3" },
    spectate: { label: "Spectate", shortcut: "4" },
    speedsters: { label: "Speedsters", shortcut: "5" },
    heal: { label: "Heal", shortcut: "J" },
    spawn: { label: "Spawn" },
    warp: { label: "Warp" },
    stats: { label: "Upgrades" },
    fakedeath: { label: "In-Game Settings" },
};

this.event = function (event) {
    let ship = event.ship;
    if (!ship || !ship.id || ship.custom.kicked || ship.custom.idle) return idleShip(ship, true, true, " ");
    switch (event.name) {
        case "ui_component_clicked":
            if (!ship.alive) return;

            if (event.id === "using_subspace" && !ship.custom.cheater) {
                ship.custom.cheater = true;
                displayMessage(ship, "You have been caught by using Subspace");
                echo(ship.name + " have been caught by using Subspace");
                if (!Game_Options.Subspace_Allow) {
                    kick(ship.id, "Using Subspace")
                }
            }

            if (!ship.custom.delay || game.step >= ship.custom.delay) {
                ship.custom.delay = game.step + 0.2 * 60;
            } else {
                return displayMessage(ship, "Take a breather, you're too fast!");
            }

            switch (event.id) {
                case "nextShip":
                    switchShip(ship, true);
                    break;
                case "previousShip":
                    switchShip(ship, false);
                    break;
                case "warp":
                    warp(ship);
                    break;
                case "spawn":
                    ship.set({ x: 0, y: 0, vx: 0, vy: 0 });
                    break;
                case "stats":
                    if (ship.custom.spectate || ship_lists.admin.includes(ship.type)) return displayMessage(ship, "You won't be able to upgrade using this ship");
                    let maxstats = Number(String(ship.type).charAt(0).repeat(8));
                    if (ship.stats < maxstats) return ship.set({ stats: maxstats });
                    ship.set({ stats: 0 });
                    break;
                case "spectate":
                    if (ship.custom.spectate) {
                        updateShip(ship, ship.custom.type);
                    } else {
                        setSpectate(ship);
                    }
                    break;
                case "speedsters":
                    updateShip(ship, ship_lists.speedster[ship_lists.speedster.indexOf(ship.type) + 1] || ship_lists.speedster[0]);
                    break;
                case "reset":
                    if (ship.type != 101) updateShip(ship, 101);
                    break;
                case "heal":
                    if (ship.custom.spectate || ship_lists.admin.includes(ship.type)) return displayMessage(ship, "You won't be able to heal using this ship");
                    ship.set({ shield: 9999, crystals: getMaxCrystals(ship.type), stats: Number(String(ship.type).charAt(0).repeat(8)) });
                    break;
                case "fakedeath":
                    if (!ship.custom.spectate) setSpectate(ship);
                    ship.intermission({});
                    break;
                case "adminShip":
                    if (!ship.custom.admin) return idleShip(ship, true, true, "You cannot get admin ship without Permission");
                    updateShip(ship, (ship_lists.admin[ship_lists.admin.indexOf(ship.type) + 1] || 605));
                    break;
                case "options":
                    if (ship.custom.option && !ship.custom.idle) {
                        let line = 0, row = 0;
                        const height = 10;
                        const width = 12;
                        const offsetX = 18.5;
                        const offsetY = 22.5;
                        const maxLine = 5;
                        const gap = 0.25;

                        Object.keys(UIs).forEach(id => {
                            const position = [offsetX + line * (width + gap), offsetY + row * (height + gap), width, height];

                            let compo = {
                                main: [
                                    { type: "box", position: [0, 0, 100, 100], fill: "hsla(210, 50%, 87%, 0.1)", stroke: "#cde0", width: 2 },
                                    { type: "box", position: [0, 0, 2.5, 100], fill: "#cde" },
                                    { type: "text", position: [10, UIs[id].shortcut ? 20 : 25, 80, 50], value: UIs[id].label || "Unknown", color: "#cde" },
                                ],
                                shortcut: [
                                    { type: "box", position: [80, 68, 50, 30], fill: "hsla(210, 50%, 87%, 0.15)" },
                                    { type: "text", position: [80, 70, 20, 30], align: "center", value: UIs[id].shortcut || "", color: "#cde" }
                                ],
                            }

                            ship.setUIComponent({
                                id,
                                position,
                                clickable: true,
                                visible: true,
                                shortcut: UIs[id].shortcut || null,
                                components: [
                                    ...compo.main,
                                    ...(UIs[id].shortcut ? compo.shortcut : [])
                                ],
                            });

                            line++;

                            if (line >= maxLine) {
                                line = 0;
                                row++;
                            }
                        });

                        ship.custom.option = false;
                    } else {
                        Object.keys(UIs).forEach(id => {
                            ship.setUIComponent({ id, ...Disable_UI });
                        });
                        ship.setUIComponent(heal);
                        ship.custom.option = true;
                    }
            }
            break;
        case "ship_disconnected":
            if (ship.custom.kicked) return;
            echo(`A player just left the game\n ${getShipData(ship)}\n`, "#ff6060");
            break;
        case "ship_destroyed":
            Object.assign(ship.custom, {
                x: ship.x,
                y: ship.y,
                type: ship.type,
            });
            if (event.killer.custom.admin && ship_lists.admin.includes(event.killer.type)) idleShip(ship, true, true);
            break;
        case "ship_spawned":
            if (ship.custom.kicked || ship.custom.idle) return;
            if (!ship.custom.registered) {
                ship.custom = {
                    x: 0,
                    y: 0,
                    type: Game_Options.Starting_ship,
                    option: true,
                    registered: true,
                };
                ship.setUIComponent(_options);
                ship.setUIComponent(heal);
                echo(`A player just joined the game\n ${getShipData(ship)}\n`);
            }
            ship.set({
                x: ship.custom.x,
                y: ship.custom.y,
                type: ship.custom.type,
                invulnerable: 300,
                shield: 9999,
                crystals: getMaxCrystals(ship.custom.type),
                stats: Number(String(ship.custom.type).charAt(0).repeat(8)),
                collider: true
            });
            break;
    }
};

function verify(id) {
    if (!id) throw new error("Invalid ship ID specified. Please provide a valid ship ID.");
    if (!game.findShip(id)) throw new error("Ship not found. Please check the ship ID and try again.");
    return game.findShip(id);
}

admin = (id) => {
    try {
        let ship = verify(id);
        if (!ship.custom.admin) {
            ship.custom.admin = true;
            ship.setUIComponent(adminn);
            echo(getShipData(ship) + " has been authorize", "#ff6060");
        } else {
            if (ship_lists.admin.includes(ship.type) || ship_lists.admin.includes(ship.custom.type)) setSpectate(ship);
            ship.setUIComponent({ id: "adminShip", ...Disable_UI });
            setTimeout(() => {
                ship.custom.admin = false;
            }, 2000);
            echo(getShipData(ship) + " has been unauthorize");
        }
    } catch { }
};

kick = (id, reason = "No reason has been provided", msg = true) => {
    try {
        let ship = verify(id);
        ship.custom = {
            kicked: true,
            registered: false
        };

        idleShip(ship, true, true, "You've been kicked");
        setTimeout(() => {
            ship.gameover({
                "You've been kicked": " ",
                "Reason": reason
            });
        }, 2000);
        if (msg) echo(`${getShipData(ship)} has been kicked\nReason: ${reason}`, "#ff6060");
    } catch { }
}

idle = (id) => {
    try {
        let ship = verify(id);
        if (ship.custom.idle) {
            idleShip(ship, false);
            echo(`${getShipData(ship)} has been Unfrozen`);
        } else {
            idleShip(ship);
            echo(`${getShipData(ship)} has been Frozen`);
        }
    } catch { }
}

